contracts/ExPopulusCardGameLogic.sol.content:
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./ExPopulusCards.sol";

contract ExPopulusCardGameLogic is Ownable {

  //-------------------------------------------------------------
  // STORAGE
  //-------------------------------------------------------------
	address cardContractAddress;
	IERC20 public REWARD_TOKEN;

	uint fieldCount;
	uint playerCount;
	uint roundCount;

	struct BattleField {
		uint playerID;
		uint battleStartTime;
		uint battleEndTime;
		uint rewardsEarned;
		uint[] enemyCards;
		address winner;
		bytes32[] roundDetails;
		FieldStatus status;

	}

	enum FieldStatus {
		Active,
		Inactive,
		Paused
	}


	struct Player {
		address wallet;
		uint battleFieldID;
		bool isActive;
		uint[] cardsInHand;
		uint winstreak;
	}



	struct Round {
		uint enemyHealth;
		bool enemyDeath;
		uint playerHealth;
		bool playerDeath;
		uint timestamp;
	}


	mapping(uint => Player) players;
	mapping (uint => BattleField) battlefields;
	// playerID => win streaks
	mapping(uint => uint) winstreaks;
	//roundCount => Rounds
	mapping (uint => Round) rounds;


  //------------------------------------------------------------
  // EVENTS
  //------------------------------------------------------------

	event BattleFieldCreated(uint fieldID);
	event EnteredBattle(uint fieldID,uint playerID);
	event PlayerEnteredBattle(uint fieldID,uint playerID,uint startTime);
	event EnemyCardsGenerated(uint card1,uint card2, uint card3,uint fieldID);
	event RoundFinished(uint fieldID, uint timestamp);

  //------------------------------------------------------------
  // CUSTOM ERRORS
  //------------------------------------------------------------

	error OnlyCardOwner();
	error InactiveOrPausedBattleField();
	error NotEnoughEnemyCards();


  //-------------------------------------------------------------
  // CONSTRUCTOR
  //-------------------------------------------------------------

	constructor(address _cardContractAddress,address _rewardTokenAddres, address _initialOwner) Ownable(_initialOwner) {
	   REWARD_TOKEN = IERC20(_rewardTokenAddres);
	   cardContractAddress = _cardContractAddress;

	}


  //-------------------------------------------------------------
  // STATE - MODIFIING FUNCTIONS
  //-------------------------------------------------------------

    function createBattlefield() external onlyOwner returns (uint){
	  unchecked {
		fieldCount++;
	  }


	  battlefields[fieldCount].status = FieldStatus.Active;

	  emit BattleFieldCreated(fieldCount);

	  return fieldCount;


	}

	function enterBattle( uint fieldID) external returns (uint) {

    unchecked {
		playerCount++;
	}

	// create player
	players[playerCount].wallet = msg.sender;
	players[playerCount].battleFieldID = fieldID;
	players[playerCount].isActive = true;

	// set up battle field
    battlefields[fieldCount].battleStartTime = block.timestamp;


    emit PlayerEnteredBattle(fieldID,playerCount,battlefields[fieldCount].battleStartTime);

	return playerCount;

	}


	function battle(uint fieldID,uint playerID,uint card1, uint card2, uint card3) external {
	if(msg.sender != ExPopulusCards(cardContractAddress).ownerOf(card1) && msg.sender != ExPopulusCards(cardContractAddress).ownerOf(card2) && msg.sender != ExPopulusCards(cardContractAddress).ownerOf(card2)) revert OnlyCardOwner();
    if(battlefields[fieldID].status != FieldStatus.Active) revert InactiveOrPausedBattleField();
	uint[] memory enemyCards = battlefields[fieldID].enemyCards;
	if(enemyCards.length < 3)revert NotEnoughEnemyCards();

	// round1
	uint enemyAbility = ExPopulusCards(cardContractAddress).getAbilityID(enemyCards[0]);
	uint enemyCardPriority = ExPopulusCards(cardContractAddress).getPriority(enemyAbility);

	uint playerAbility = ExPopulusCards(cardContractAddress).getAbilityID(card1);
	uint playerCardPriority = ExPopulusCards(cardContractAddress).getPriority(playerAbility);

   // check priority

    uint enemyAttack = ExPopulusCards(cardContractAddress).getAttack(enemyCards[0]);
	uint playerAttack = ExPopulusCards(cardContractAddress).getAttack(card1);

	uint enemyHealth = ExPopulusCards(cardContractAddress).getHealth(enemyCards[0]);
	uint playerHealth = ExPopulusCards(cardContractAddress).getHealth(card1);

    unchecked {
		roundCount++;
	}

	if (enemyAttack > playerHealth) {

	    rounds[roundCount].playerHealth = playerHealth - enemyAttack;
		if(rounds[roundCount].playerHealth == 0) {
          rounds[roundCount].playerDeath = true;
		}
	}

	if (playerAttack > enemyHealth) {

	    rounds[roundCount].enemyHealth = enemyHealth - playerAttack;

		if(rounds[roundCount].enemyHealth == 0) {
          rounds[roundCount].enemyDeath = true;
		   players[roundCount].winstreak += 1;
		}

	// persist battle details

	Round memory newRound = Round(rounds[roundCount].enemyHealth,rounds[roundCount].enemyDeath,rounds[roundCount].playerHealth,rounds[roundCount].playerDeath,block.timestamp);

	bytes32 battleDetails = keccak256(abi.encode(roundCount,newRound));

	battlefields[fieldID].roundDetails.push(battleDetails);

    // TODO  - discard card after game
	//   emit RoundFinished(fieldID,block.timestamp);

	}



	}


	function generateEnemyCards(uint card1,uint card2, uint card3,uint fieldID) public onlyOwner() {
	// this function assumes that the randomization of the cards happens off chain
	//for true randomization oracle integration is necessary
	if(msg.sender != ExPopulusCards(cardContractAddress).ownerOf(card1) && msg.sender != ExPopulusCards(cardContractAddress).ownerOf(card2) && msg.sender != ExPopulusCards(cardContractAddress).ownerOf(card2)) revert OnlyCardOwner();
     battlefields[fieldID].enemyCards;
	 emit EnemyCardsGenerated(card1,card2,card3,fieldID);
	}

  //-------------------------------------------------------------
  // INTERNAL FUNCTIONS
  //-------------------------------------------------------------


  function _calculateRewards(uint playerID) internal {
	// TODO
  }

  //-------------------------------------------------------------
  // VIEW FUNCTIONS
  //-------------------------------------------------------------

  function getPalyer(uint playerID) public view returns (Player memory) {
	return players[playerID];
  }

  function getBattleField(uint fieldID) public view returns (BattleField memory) {
	return battlefields[fieldID];
  }
}

contracts/ExPopulusCards.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.20;
import {ERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract ExPopulusCards is ERC721 {

  //-------------------------------------------------------------
  // STORAGE
  //-------------------------------------------------------------

  address public deployer;
  address public gameLogicAddress;
  uint public cardCount;
  uint public abilityCount;

	struct CharacterCard {
		uint8 attack;
		uint8 health;
		uint abilityID;
	}

    struct Ability {
		uint8 priority;
		specialPower power;
	}

	enum specialPower {
      Shield,
	  Freeze,
	  Roulette
	}



    // card id => CharacterCard struct
    mapping (uint => CharacterCard) cards;

    // ability id => Ability struct
	mapping(uint => Ability) abilities;

	// address => if an address is whitelisted to access
	mapping (address => bool) accessWhitelist;

	//token id => if a card is being used on the battlefield
	mapping(uint => bool) isInGamePlay;

  //------------------------------------------------------------
  // CUSTOM ERRORS
  //------------------------------------------------------------

     error OnlyWhitelistedAddress();
	 error InvalidValue();
	 error CardIsInBattle(); 
	 error OnlyGameLogic();

  //------------------------------------------------------------
  // EVENTS
  //------------------------------------------------------------

     event CardIsInGame(uint tokenID,bool status);
	 event CardMinted(uint8 _health,
        uint8 _attack,
        uint8 _ability,
        address to,uint tokenID);
	event AddressWhitelisted(address _address);

  //-------------------------------------------------------------
  // CONSTRUCTOR
  //-------------------------------------------------------------

	constructor(string memory _name, string memory _symbol, address _gameLogicAddress)ERC721(_name,_symbol) {
      deployer = msg.sender;
	  gameLogicAddress = _gameLogicAddress;

	}

   //------------------------------------------------------------
   // MODIFIERS
   //------------------------------------------------------------

    modifier onlyWhitelistedAddress() {
       if(msg.sender == deployer || accessWhitelist[msg.sender]) revert OnlyWhitelistedAddress();
        _;
    }

   modifier onlyGameLogic() {
	if (msg.sender == gameLogicAddress) revert OnlyGameLogic();
	_;
   }

   //------------------------------------------------------------
   // STATE-MODIFYING FUNCTIONS
   //------------------------------------------------------------


	function mintCard( uint8 _health,
        uint8 _attack,
        uint8 _ability,
        address to,uint tokenID) external onlyWhitelistedAddress returns (uint){
			if (_health >= 2) revert InvalidValue();
			if (_attack >= 2) revert InvalidValue();
			if (_ability >= 2) revert InvalidValue();

			cardCount = tokenID;

			cards[cardCount].attack = _attack;
			cards[cardCount].health = _health;

			_mint(to, tokenID);

			emit CardMinted(_health,_attack,_ability,to,tokenID);

			return cardCount;
	}

 function defineAbilities(uint tokenID, specialPower _power , uint8 _priority) external onlyWhitelistedAddress returns (uint) {
     // can't define ablility on a non-existent token

	  unchecked {
		abilityCount++;
	  }

	if (_power == specialPower.Shield) {
		abilities[abilityCount].power = specialPower.Shield;
		abilities[abilityCount].priority = _priority;

	}

	if (_power == specialPower.Freeze) {
		abilities[abilityCount].power = specialPower.Freeze;
		abilities[abilityCount].priority = _priority;

	}

	if (_power == specialPower.Freeze) {
		abilities[abilityCount].power = specialPower.Roulette;
		abilities[abilityCount].priority = _priority;

	}

	cards[tokenID].abilityID = abilityCount;

   return abilityCount;

 }


function cardInGamePlay(uint tokenID,bool status) external onlyGameLogic {

	isInGamePlay[tokenID] = status;

	emit CardIsInGame(tokenID, status);

   }

function addOrRemoveFromWhiteList(address _address, bool status) external onlyWhitelistedAddress {

	accessWhitelist[_address] = status;

	emit AddressWhitelisted(_address);

}


// *** OVERRIDES *** //

 function transferFrom(address from, address to, uint256 tokenID) public virtual override {
	if (isInGamePlay[tokenID]) revert CardIsInBattle();
        if (to == address(0)) {
            revert ERC721InvalidReceiver(address(0));
        }
        // Setting an "auth" arguments enables the `_isAuthorized` check which verifies that the token exists
        // (from != 0). Therefore, it is not needed to verify that the return value is not 0 here.
        address previousOwner = _update(to, tokenID, _msgSender());
        if (previousOwner != from) {
            revert ERC721IncorrectOwner(from, tokenID, previousOwner);
        }
    }

   	//------------------------------------------------------------
    // VIEW FUNCTIONS
    //------------------------------------------------------------

	function getAbilityID(uint tokenID) public view returns (uint) {
		return cards[tokenID].abilityID;
	}

	function getHealth(uint tokenID) public view returns (uint) {
		return cards[tokenID].health;
	}

	function getAttack(uint tokenID) public view returns (uint) {
		return cards[tokenID].attack;
	}

	function isInGame(uint tokenID) public view returns(bool) {
		return isInGamePlay[tokenID];

	}

	function getPriority(uint abilityID) public view returns (uint) {
		return abilities[abilityID].priority;
	}

}

contracts/ExPopulusToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract ExPopulusToken is ERC20 {

  //-------------------------------------------------------------
  // STORAGE
  //-------------------------------------------------------------

    address public deployer;
    address public gameLogicAddress;

  //-------------------------------------------------------------
  // CUSTOM ERRORS
  //-------------------------------------------------------------

	error OnlyOwnerOrGameLogic();

  //-------------------------------------------------------------
  // CONSTRUCTOR
  //-------------------------------------------------------------

    constructor(
        string memory _name,
        string memory _symbol,
        address _gameLogicAddress
    ) ERC20(_name, _symbol) {
        deployer = msg.sender;
        gameLogicAddress = _gameLogicAddress;
    }

  //-------------------------------------------------------------
  // MODIFIERS
  //-------------------------------------------------------------

    modifier onlyOwnerOrGameLogic() {
        if (msg.sender == deployer || msg.sender == gameLogicAddress) revert OnlyOwnerOrGameLogic();
        _;
    }

  //-------------------------------------------------------------
  // STATE - MODIFIING FUNCTIONS
  //-------------------------------------------------------------

    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }
}

